class Auth {
  constructor(options) {
    this.options = options;
  }

  register(email, password) {
    return (
      fetch(`${this.options.baseUrl}/signup`, {
        method: "POST",
        headers: {
          Acccept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      })
        //  .then((res) => {
        //    return res.json();
        //  });
        .then((res) => {
          if (res.status === 201) {
            return res.json();
          } else {
            throw new Error("409 - Unsuccessful registration");
          }
        })
    );
  }

  authorize(userid, password) {
    return fetch(`${this.options.baseUrl}/signin`, {
      method: "POST",
      headers: {
        Acccept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email: userid, password: password }),
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        if (!data.message) {
          localStorage.setItem("token", data.token);
          return data;
          // } else {
          //   return;
        }
      });
  }

  getContent(token) {
    return fetch(`${this.options.baseUrl}/users/me`, {
      method: "GET",
      headers: {
        Acccept: "application/json",
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
    })
      .then((res) => {
        return res.ok
          ? res.json()
          : Promise.reject(`${res.status} - ${res.message}`);
      })
      .then((data) => {
        return data;
      })
      .catch((err) => console.log(err));
  }
}
const auth = new Auth({
  baseUrl: "https://api.stephanydev.justlearning.net",
});

export default auth;
